import React, { useEffect, useState, useCallback } from "react";
import { Card, CardContent } from "../components/ui/card";
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  Tooltip,
  ResponsiveContainer,
  PieChart,
  Pie,
  Cell,
} from "recharts";
import { Input } from "../components/ui/input";
import { Button } from "../components/ui/button";
import { Moon, Sun, Home, RefreshCw } from "lucide-react";
import axios from "axios";
import { useToast } from "../components/ui/use-toast";

const COLORS = [
  "#FF8C00", // Oranje
  "#1E3A8A", // Donkerblauw
  "#60A5FA", // Lichtblauw
];

const CryptoDashboard = () => {
  const [coins, setCoins] = useState([]);
  const [search, setSearch] = useState("");
  const [loading, setLoading] = useState(true);
  const [theme, setTheme] = useState("light");
  const [username, setUsername] = useState("Meester");
  const [error, setError] = useState(null);
  const [wallet, setWallet] = useState({
    balance: 1000, // Startsaldo
    holdings: {}, // Bezittingen per cryptocurrency
  });
  const { toast } = useToast();

  const fetchCoins = useCallback(async () => {
    try {
      setLoading(true);
      setError(null);

      const response = await axios.get(
        "https://api.coingecko.com/api/v3/coins/markets",
        {
          params: {
            vs_currency: "usd",
            order: "market_cap_desc",
            per_page: 10,
            page: 1,
            sparkline: true,
          },
        }
      );

      setCoins(response.data);
    } catch (error) {
      setError("Er is een fout opgetreden bij het ophalen van de data.");
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    fetchCoins();
  }, [fetchCoins]);

  const handleBuy = (coin) => {
    const price = coin.current_price;
    if (wallet.balance >= price) {
      setWallet((prevWallet) => ({
        balance: prevWallet.balance - price,
        holdings: {
          ...prevWallet.holdings,
          [coin.id]: (prevWallet.holdings[coin.id] || 0) + 1,
        },
      }));
      toast({
        title: "Koop Order",
        description: `Je hebt 1 ${coin.name} gekocht voor $${price.toFixed(2)}.`,
      });
    } else {
      toast({
        title: "Onvoldoende saldo",
        description: "Je hebt niet genoeg saldo om deze aankoop te doen.",
        variant: "destructive",
      });
    }
  };

  const handleSell = (coin) => {
    if (wallet.holdings[coin.id] > 0) {
      const price = coin.current_price;
      setWallet((prevWallet) => ({
        balance: prevWallet.balance + price,
        holdings: {
          ...prevWallet.holdings,
          [coin.id]: prevWallet.holdings[coin.id] - 1,
        },
      }));
      toast({
        title: "Verkoop Order",
        description: `Je hebt 1 ${coin.name} verkocht voor $${price.toFixed(2)}.`,
      });
    } else {
      toast({
        title: "Onvoldoende bezittingen",
        description: `Je hebt geen ${coin.name} om te verkopen.`,
        variant: "destructive",
      });
    }
  };

  const filteredCoins = coins.filter((coin) =>
    coin.name.toLowerCase().includes(search.toLowerCase())
  );

  const marketData = coins.map((coin) => ({
    name: coin.name,
    value: coin.market_cap,
    symbol: coin.symbol.toUpperCase(),
  }));

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex justify-between items-center">
        <div className="flex items-center gap-3">
          <img src="/crypto-logo.svg" alt="Crypto Logo" className="w-24 h-24" />
          <div>
            <h1 className="text-3xl font-bold">NebulaBytes Dashboard</h1>
            <p className="text-lg text-muted-foreground">
              Welkom terug, {username}!
            </p>
          </div>
        </div>
        <div className="flex items-center gap-2">
          <Button
            variant="outline"
            size="icon"
            onClick={() => (window.location.href = "/.")}
            className="hover:bg-primary hover:text-primary-foreground"
          >
            <Home className="h-5 w-5" />
          </Button>
          <Button
            variant="outline"
            size="icon"
            onClick={() => {
              const newTheme = theme === "dark" ? "light" : "dark";
              setTheme(newTheme);
              document.documentElement.classList.toggle("dark");
            }}
          >
            {theme === "dark" ? (
              <Sun className="h-5 w-5" />
            ) : (
              <Moon className="h-5 w-5" />
            )}
          </Button>
        </div>
      </div>

      {/* Wallet Section */}
      <div className="bg-white dark:bg-gray-800 rounded-lg p-4 shadow-lg">
        <h2 className="text-xl font-bold mb-4">Wallet</h2>
        <p className="text-lg">Saldo: ${wallet.balance.toFixed(2)}</p>
        <div className="mt-4">
          <h3 className="text-lg font-semibold">Bezittingen:</h3>
          {Object.keys(wallet.holdings).length > 0 ? (
            <ul className="list-disc list-inside">
              {Object.entries(wallet.holdings).map(([coinId, amount]) => {
                const coin = coins.find((c) => c.id === coinId);
                return (
                  <li key={coinId}>
                    {coin?.name || coinId}: {amount} stuks
                  </li>
                );
              })}
            </ul>
          ) : (
            <p className="text-muted-foreground">Geen bezittingen.</p>
          )}
        </div>
      </div>

      {/* Coins Section */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {filteredCoins.map((coin) => (
          <Card key={coin.id} className="hover:shadow-lg transition-shadow">
            <CardContent className="p-4">
              <div className="flex items-center justify-between mb-4">
                <div className="flex items-center gap-2">
                  <img src={coin.image} alt={coin.name} className="w-8 h-8" />
                  <div>
                    <h2 className="text-lg font-semibold">{coin.name}</h2>
                    <p className="text-sm text-muted-foreground">
                      {coin.symbol.toUpperCase()}
                    </p>
                  </div>
                </div>
                <div className="text-right">
                  <p className="text-lg font-bold">
                    ${coin.current_price.toFixed(2)}
                  </p>
                </div>
              </div>
              <div className="flex gap-2">
                <Button
                  className="flex-1 bg-green-500 hover:bg-green-600 text-white"
                  onClick={() => handleBuy(coin)}
                >
                  Koop
                </Button>
                <Button
                  className="flex-1 bg-red-500 hover:bg-red-600 text-white"
                  onClick={() => handleSell(coin)}
                >
                  Verkoop
                </Button>
              </div>
            </CardContent>
          </Card>
        ))}
      </div>
    </div>
  );
};

export default CryptoDashboard;
