import React, { useState } from "react";
import { Card, CardContent } from "../components/ui/card";
import { Button } from "../components/ui/button";

const Wallet = ({ user, balance }) => {
    const [walletBalance, setWalletBalance] = useState(balance);
    const [transactionHistory, setTransactionHistory] = useState([]);

    const addTransaction = (amount) => {
        const newBalance = walletBalance + amount;
        setWalletBalance(newBalance);
        setTransactionHistory([...transactionHistory, { amount, date: new Date() }]);
    };

    return (
        <div className="container mx-auto py-6 space-y-6">
            <Card>
                <CardContent className="p-6">
                    <h1 className="text-3xl font-bold mb-4">Wallet</h1>
                    <p className="text-lg mb-2">User: {user}</p>
                    <p className="text-lg mb-4">Balance: ${walletBalance}</p>
                    <div className="flex gap-2 mb-4">
                        <Button onClick={() => addTransaction(100)} className="bg-green-500 hover:bg-green-600 text-white">
                            Add $100
                        </Button>
                        <Button onClick={() => addTransaction(-50)} className="bg-red-500 hover:bg-red-600 text-white">
                            Subtract $50
                        </Button>
                    </div>
                    <h2 className="text-2xl font-bold mb-2">Transaction History</h2>
                    <ul className="space-y-2">
                        {transactionHistory.map((transaction, index) => (
                            <li key={index} className="border-b py-2">
                                {transaction.amount > 0 ? "Added" : "Subtracted"} ${Math.abs(transaction.amount)} on {transaction.date.toLocaleString()}
                            </li>
                        ))}
                    </ul>
                </CardContent>
            </Card>
        </div>
    );
};

export default Wallet;
