import React, { useEffect, useState, useCallback } from "react";
import { Card, CardContent } from "../components/ui/card";

import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  Tooltip,
  ResponsiveContainer,
  PieChart,
  Pie,
  Cell,
  Legend,
} from "recharts";
import { Input } from "../components/ui/input";
import { Button } from "../components/ui/button";
import { Moon, Sun, Home, RefreshCw } from "lucide-react";
import axios from "axios";
import { useToast } from "../components/ui/use-toast";
import { Link } from "react-router-dom";
const COLORS = [
  "#FF8C00", // Oranje
  "#1E3A8A", // Donkerblauw
  "#60A5FA", // Lichtblauw
  "#FF8C00", // Oranje
  "#1E3A8A", // Donkerblauw
  "#60A5FA", // Lichtblauw
  "#FF8C00", // Oranje
  "#1E3A8A", // Donkerblauw
  "#60A5FA", // Lichtblauw
  "#FF8C00", // Oranje
];

const CryptoDashboard = () => {
  const [coins, setCoins] = useState([]);
  const [favorites, setFavorites] = useState([]);

  const toggleFavorite = (coin) => {
    setFavorites((prevFavorites) =>
      prevFavorites.some((fav) => fav.id === coin.id)
        ? prevFavorites.filter((fav) => fav.id !== coin.id)
        : [...prevFavorites, coin]
    );
  };
  const [search, setSearch] = useState("");
  const [loading, setLoading] = useState(true);
  const [theme, setTheme] = useState("light");
  const [username, setUsername] = useState("Meester");
  const [error, setError] = useState(null);
  const { toast } = useToast();

  const fetchCoins = useCallback(
    async (retryCount = 0) => {
      try {
        setLoading(true);
        setError(null);

        const response = await axios.get(
          "https://api.coingecko.com/api/v3/coins/markets",
          {
            params: {
              vs_currency: "usd",
              order: "market_cap_desc",
              per_page: 10,
              page: 1,
              sparkline: true,
            },
            timeout: 10000, // 10 seconden timeout
            headers: {
              Accept: "application/json",
            },
          }
        );

        if (!response.data || !Array.isArray(response.data)) {
          throw new Error("Ongeldige data ontvangen van de API");
        }

        setCoins(response.data);
        toast({
          title: "Data bijgewerkt",
          description: "De cryptocurrency data is succesvol bijgewerkt.",
        });
      } catch (error) {
        console.error("Error fetching data:", error);

        // Controleer of het een netwerkfout is
        if (error.code === "ECONNABORTED" || !error.response) {
          if (retryCount < 3) {
            // Wacht 2 seconden en probeer opnieuw
            setTimeout(() => {
              fetchCoins(retryCount + 1);
            }, 2000);
            return;
          }
        }

        // Toon specifieke foutmeldingen
        let errorMessage =
          "Er is een fout opgetreden bij het ophalen van de data.";
        if (error.response?.status === 429) {
          errorMessage =
            "Te veel verzoeken. Probeer het over een paar minuten opnieuw.";
        } else if (error.response?.status === 404) {
          errorMessage = "De API endpoint is niet gevonden.";
        }

        setError(errorMessage);
        toast({
          title: "Fout",
          description: errorMessage,
          variant: "destructive",
        });
      } finally {
        setLoading(false);
      }
    },
    [toast]
  );

  useEffect(() => {
    fetchCoins();
  }, [fetchCoins]);

  const handleBuy = (coin) => {
    toast({
      title: "Koop Order",
      description: `Je hebt een koop order geplaatst voor ${
        coin.name
      } (${coin.symbol.toUpperCase()})`,
    });
  };

  const handleSell = (coin) => {
    toast({
      title: "Verkoop Order",
      description: `Je hebt een verkoop order geplaatst voor ${
        coin.name
      } (${coin.symbol.toUpperCase()})`,
    });
  };

  const filteredCoins = coins.filter((coin) =>
    coin.name.toLowerCase().includes(search.toLowerCase())
  );

  const marketData = coins.map((coin) => ({
    name: coin.name,
    value: coin.market_cap,
    symbol: coin.symbol.toUpperCase(),
  }));

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <div className="flex items-center gap-3">
          <img src="/crypto-logo.svg" alt="Crypto Logo" className="w-24 h-24" />
          <div>
            <h1 className="text-3xl font-bold">NebulaBytes Dashboard</h1>
            <p className="text-lg text-muted-foreground">
              Welkom terug, {username}!
            </p>
          </div>
        </div>
        <div className="flex items-center gap-2">
          <Link to="/HomePage">
            <Button
              variant="outline"
              size="icon"
              className="hover:bg-primary hover:text-primary-foreground"
            >
              <Home className="h-5 w-5" />
            </Button>
          </Link>
          <Button
            variant="outline"
            size="icon"
            onClick={() => {
              const newTheme = theme === "dark" ? "light" : "dark";
              setTheme(newTheme);
              document.documentElement.classList.toggle("dark");
            }}
          >
            {theme === "dark" ? (
              <Sun className="h-5 w-5" />
            ) : (
              <Moon className="h-5 w-5" />
            )}
          </Button>
        </div>
      </div>

      {/* Rest of the code remains unchanged */}
    </div>
  );
};

export default CryptoDashboard;
