import React, { useState } from "react";

const Wallet = ({ user, balance }) => {
    const [walletBalance, setWalletBalance] = useState(balance);
    const [transactionHistory, setTransactionHistory] = useState([]);

    const addTransaction = (amount) => {
        const newBalance = walletBalance + amount;
        setWalletBalance(newBalance);
        setTransactionHistory([...transactionHistory, { amount, date: new Date() }]);
    };

    return (
        <div>
            <h1>Wallet</h1>
            <p>User: {user}</p>
            <p>Balance: ${walletBalance}</p>
            <button onClick={() => addTransaction(100)}>Add $100</button>
            <button onClick={() => addTransaction(-50)}>Subtract $50</button>
            <h2>Transaction History</h2>
            <ul>
                {transactionHistory.map((transaction, index) => (
                    <li key={index}>
                        {transaction.amount > 0 ? "Added" : "Subtracted"} ${Math.abs(transaction.amount)} on {transaction.date.toLocaleString()}
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default Wallet;
